'use strict';

var supercluster = require('./');

// console.time('parsing JSON');
var points = require('./trees-na.json');
// console.timeEnd('parsing JSON');

// console.time('mapping to GeoJSON');
points = points.map(function (p) {
    return {
        type: 'Feature',
        properties: {},
        geometry: {
            type: 'Point',
            coordinates: p
        }
    };
});
// console.timeEnd('mapping to GeoJSON');

console.log(JSON.stringify({type: "FeatureCollection", features: points}));

